export default class SearchTokenizerResult{constructor(){this.tokens=[]}getTag(t){return this.tokens.filter((e=>e.tag===t))}getTaggedTerms(){return this.tokens.filter((t=>t.tag!==null))}getUntaggedTerms(){return this.tokens.filter((t=>t.tag===null))}getFullPhrase(){let t="";this.getUntaggedTerms().forEach((e=>t+=" "+e.term));return t.trim()}}